import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  // Pod state
  const [pods, setPods] = useState<{ healthy: any[]; unhealthy: any[] }>({ healthy: [], unhealthy: [] });
  const [loading, setLoading] = useState(true);
  const [analysis, setAnalysis] = useState<any>(null);
  const [analyzingPod, setAnalyzingPod] = useState<{ name: string; namespace: string } | null>(null);
  const [showModal, setShowModal] = useState(false);

  // Fetch pods
  useEffect(() => {
    fetch('/pods')
      .then((res) => res.json())
      .then((data) => {
        setPods(data);
        setLoading(false);
      });
  }, []);

  // Analyze pod
  const handleAnalyze = async (pod: { name: string; namespace: string }) => {
    setAnalyzingPod(pod);
    setShowModal(true);
    setAnalysis(null);
    try {
      const res = await fetch('/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ pod_name: pod.name, namespace: pod.namespace }),
      });
      const data = await res.json();
      setAnalysis(data);
    } catch (err) {
      setAnalysis({ error: String(err) });
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h1 className="text-3xl font-bold mb-6 text-center">Kubernetes Pod Health Analyzer</h1>
      {loading ? (
        <div className="text-center">Loading pods...</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Healthy Pods */}
          <div>
            <h2 className="text-xl font-semibold mb-4 flex items-center gap-2">
              <span className="text-green-500">✅</span> Healthy Pods
            </h2>
            <ul className="space-y-2">
              {pods.healthy.map((pod) => (
                <li key={pod.name + pod.namespace} className="bg-white rounded shadow p-4 flex items-center">
                  <span className="font-medium">{pod.name}</span>
                  <span className="ml-2 text-xs text-gray-500">({pod.namespace})</span>
                </li>
              ))}
              {pods.healthy.length === 0 && <li className="text-gray-400">No healthy pods</li>}
            </ul>
          </div>
          {/* Unhealthy Pods */}
          <div>
            <h2 className="text-xl font-semibold mb-4 flex items-center gap-2">
              <span className="text-red-500">❌</span> Unhealthy Pods
            </h2>
            <ul className="space-y-2">
              {pods.unhealthy.map((pod) => (
                <li key={pod.name + pod.namespace} className="bg-white rounded shadow p-4 flex items-center justify-between">
                  <div>
                    <span className="font-medium">{pod.name}</span>
                    <span className="ml-2 text-xs text-gray-500">({pod.namespace})</span>
                  </div>
                  <button
                    className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
                    onClick={() => handleAnalyze(pod)}
                  >
                    Analyze
                  </button>
                </li>
              ))}
              {pods.unhealthy.length === 0 && <li className="text-gray-400">No unhealthy pods</li>}
            </ul>
          </div>
        </div>
      )}
      {/* Modal for analysis */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-lg relative">
            <button
              className="absolute top-2 right-2 text-gray-400 hover:text-gray-700"
              onClick={() => setShowModal(false)}
            >
              ✕
            </button>
            <h3 className="text-lg font-bold mb-4">Analysis for {analyzingPod?.name}</h3>
            {!analysis ? (
              <div>Analyzing...</div>
            ) : (
              <div>
                {/* Always show the full backend response for debugging */}
                <div className="mb-4">
                  <div className="font-semibold mb-1">Raw Backend Response:</div>
                  <pre className="text-xs mt-2 whitespace-pre-wrap bg-gray-100 rounded p-3">
                    {JSON.stringify(analysis, null, 2)}
                  </pre>
                </div>
                {/* Show issues if present */}
                {Array.isArray(analysis.analysis) && analysis.analysis.length > 0 ? (
                  <ul className="space-y-2">
                    {analysis.analysis.map((item: any, idx: number) => (
                      <li key={idx} className="bg-gray-100 rounded p-3">
                        <div className="font-semibold">{item.name}</div>
                        <pre className="text-xs mt-2 whitespace-pre-wrap">{JSON.stringify(item.issues, null, 2)}</pre>
                      </li>
                    ))}
                  </ul>
                ) : analysis.error ? (
                  <div className="text-red-500">Error: {analysis.error}</div>
                ) : (
                  <div>No issues found.</div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
