import React, { useState } from "react";
import axios from "axios";

function App() {
  const [namespace, setNamespace] = useState("default");
  const [podName, setPodName] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [error, setError] = useState(null);

  const analyzePod = async () => {
    setError(null);
    try {
      const response = await axios.post("http://localhost:8000/analyze", {
        namespace,
        pod_name: podName,
      });
      setAnalysis(response.data);
    } catch (err) {
      setError("Error analyzing pod");
      console.error(err);
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Pod Analyzer</h2>
      <input
        type="text"
        placeholder="Namespace"
        value={namespace}
        onChange={(e) => setNamespace(e.target.value)}
        style={{ marginRight: "1rem" }}
      />
      <input
        type="text"
        placeholder="Pod Name"
        value={podName}
        onChange={(e) => setPodName(e.target.value)}
        style={{ marginRight: "1rem" }}
      />
      <button onClick={analyzePod}>Analyze</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {analysis && (
        <div style={{ marginTop: "2rem" }}>
          <h3>Analysis Result</h3>
          <p><strong>Pod:</strong> {analysis.pod}</p>
          <p><strong>Status:</strong> {analysis.analysis?.status}</p>
          <p><strong>Responsibility:</strong> {analysis.responsibility || "Unknown"}</p>

          {analysis.analysis?.results?.length > 0 ? (
            <ul>
              {analysis.analysis.results.map((res, idx) => (
                <li key={idx} style={{ marginBottom: "1rem" }}>
                  <p><strong>Kind:</strong> {res.kind}</p>
                  <p><strong>Name:</strong> {res.name}</p>
                  <p><strong>Details:</strong> {res.details}</p>
                  {res.error?.length > 0 && (
                    <ul>
                      {res.error.map((errItem, i) => (
                        <li key={i}>{errItem.Text}</li>
                      ))}
                    </ul>
                  )}
                </li>
              ))}
            </ul>
          ) : (
            <p>No issues detected or no results returned.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
