import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [pods, setPods] = useState<any[]>([]);
  const [loading, setLoading] = useState(false);
  const [analyzingPod, setAnalyzingPod] = useState<string | null>(null);
  const [analysis, setAnalysis] = useState<any>(null);
  const [showModal, setShowModal] = useState(false);
  const [clusterStatus, setClusterStatus] = useState<string>('');

  const fetchPods = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:8000/get_pods');
      const data = await response.json();
      setPods(data.pods || []);
    } catch (error) {
      console.error('Error fetching pods:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchClusterStatus = async () => {
    try {
      const response = await fetch('http://localhost:8000/check_cluster');
      const data = await response.json();
      setClusterStatus(data.message);
    } catch (error) {
      console.error('Error checking cluster:', error);
    }
  };

  const analyzePod = async (podName: string) => {
    setAnalyzingPod(podName);
    setShowModal(true);
    setAnalysis(null);
    try {
      const response = await fetch(`http://localhost:8000/analyze?pod_name=${podName}`);
      const data = await response.json();
      setAnalysis(data);
    } catch (error) {
      console.error('Error analyzing pod:', error);
    }
  };

  useEffect(() => {
    fetchPods();
    fetchClusterStatus();
  }, []);

  const renderMainPodIssue = (res: any) => (
    <div>
      <h3>Analysis Result</h3>
      {res && res.main_issue && <p><strong>Main Issue:</strong> {res.main_issue}</p>}
      {res && res.details && (
        <p><strong>Details:</strong> {res.details}</p>
      )}
      {res.error && res.error.length > 0 && (
        <ul>
          {res.error.map((errItem: any, i: number) => (
            <li key={i}>{errItem.Text}</li>
          ))}
        </ul>
      )}
    </div>
  );

  const renderFixSuggestions = (fixes: any) => (
    <div>
      <h3>Suggested Fixes</h3>
      {fixes && Array.isArray(fixes) && fixes.length > 0 ? (
        <ul>
          {fixes.map((fix: string, i: number) => (
            <li key={i}>{fix}</li>
          ))}
        </ul>
      ) : (
        <p>No suggestions available.</p>
      )}
    </div>
  );

  const renderExtraAnalysisInfo = (res: any) => {
    const responsibility = res?.responsibility;
    return (
      <div>
        {responsibility && (
          <p>
            <strong>Responsibility:</strong>{' '}
            <span
              style={{
                color: responsibility === 'DevOps' ? 'green' : 'purple',
                fontWeight: 'bold',
              }}
            >
              {responsibility}
            </span>
          </p>
        )}
      </div>
    );
  };

  return (
    <div className="App">
      <h1>Kubernetes Pod Analyzer</h1>
      <p><strong>Cluster Status:</strong> {clusterStatus}</p>
      <button onClick={fetchPods} disabled={loading}>
        {loading ? 'Refreshing...' : 'Refresh Pods'}
      </button>
      <ul>
        {pods.map((pod, index) => (
          <li key={index}>
            {pod.name} - <button onClick={() => analyzePod(pod.name)}>Analyze</button>
          </li>
        ))}
      </ul>

      {showModal && analyzingPod && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowModal(false)}>&times;</span>
            <h2>Analysis for {analyzingPod}</h2>
            {analysis ? (
              <div>
                {renderExtraAnalysisInfo(analysis)}
                {renderMainPodIssue(analysis)}
                {renderFixSuggestions(analysis?.suggested_fixes)}
              </div>
            ) : (
              <p>Loading analysis...</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
