import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  // Pod state
  const [pods, setPods] = useState<{ healthy: any[]; unhealthy: any[] }>({ healthy: [], unhealthy: [] });
  const [loading, setLoading] = useState(true);
  const [analysis, setAnalysis] = useState<any>(null);
  const [analyzingPod, setAnalyzingPod] = useState<{ name: string; namespace: string } | null>(null);
  const [showModal, setShowModal] = useState(false);

  // Fetch pods
  useEffect(() => {
    fetch('/pods')
      .then((res) => res.json())
      .then((data) => {
        setPods(data);
        setLoading(false);
      });
  }, []);

  // Analyze pod
  const handleAnalyze = async (pod: { name: string; namespace: string }) => {
    // Only fetch new analysis if a different pod is selected
    if (analyzingPod && analyzingPod.name === pod.name && analyzingPod.namespace === pod.namespace && analysis) {
      setShowModal(true);
      return;
    }
    setAnalyzingPod(pod);
    setShowModal(true);
    setAnalysis(null);
    try {
      const res = await fetch('/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ pod_name: pod.name, namespace: pod.namespace }),
      });
      const data = await res.json();
      setAnalysis(data);
    } catch (err) {
      setAnalysis({ error: String(err) });
    }
  };

  // Helper to render only the main Pod issue in a clear format
  function renderMainPodIssue(analysis: any) {
    if (!analysis || !analysis.analysis || !analysis.analysis.results) return null;
    // Find the first Pod kind result
    const podResult = analysis.analysis.results.find((r: any) => r.kind === 'Pod');
    if (!podResult) return null;
    const mainError = Array.isArray(podResult.error) && podResult.error.length > 0 ? podResult.error[0].Text : null;
    const details = podResult.details ? podResult.details.trim() : null;
    return (
      <div className="main-pod-issue-block">
        <div className="main-pod-issue-title">Pod: <span className="main-pod-issue-name">{podResult.name}</span></div>
        {mainError && <div className="main-pod-issue-error">{mainError}</div>}
        {details && (
          <div className="main-pod-issue-details">
            <strong>How to fix:</strong>
            <pre className="main-pod-issue-solution">{details}</pre>
          </div>
        )}
      </div>
    );
  }

  return (
    <div className="app-bg">
      <h1 className="app-title">HealOps - Kubernets AI Powered Analyzer</h1>
      {loading ? (
        <div className="loading">Loading pods...</div>
      ) : (
        <div className="pod-sections">
          {/* Healthy Pods */}
          <div className="pod-card healthy">
            <h2 className="pod-section-title healthy">✅ Healthy Pods</h2>
            <ul className="pod-list">
              {pods.healthy.map((pod) => (
                <li key={pod.name + pod.namespace} className="pod-list-item healthy">
                  <span className="pod-name">{pod.name}</span>
                  <span className="pod-namespace">({pod.namespace})</span>
                </li>
              ))}
              {pods.healthy.length === 0 && <li className="pod-empty">No healthy pods</li>}
            </ul>
          </div>
          {/* Unhealthy Pods */}
          <div className="pod-card unhealthy">
            <h2 className="pod-section-title unhealthy">❌ Unhealthy Pods</h2>
            <ul className="pod-list">
              {pods.unhealthy.map((pod) => (
                <li key={pod.name + pod.namespace} className="pod-list-item unhealthy">
                  <div>
                    <span className="pod-name">{pod.name}</span>
                    <span className="pod-namespace">({pod.namespace})</span>
                  </div>
                  <button
                    className="analyze-btn"
                    onClick={() => handleAnalyze(pod)}
                  >
                    Analyze
                  </button>
                </li>
              ))}
              {pods.unhealthy.length === 0 && <li className="pod-empty">No unhealthy pods</li>}
            </ul>
          </div>
        </div>
      )}
      {/* Modal for analysis */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-window">
            <button
              className="modal-close"
              onClick={() => setShowModal(false)}
              aria-label="Close analysis modal"
            >
              ×
            </button>
            <h3 className="modal-title">Analysis for <span className="modal-pod-name">{analyzingPod?.name}</span></h3>
            {!analysis ? (
              <div className="modal-loading">Analyzing...</div>
            ) : (
              <div>
                {/* Processed, user-friendly analysis */}
                {renderMainPodIssue(analysis)}
                {/* Toggle raw response for debugging */}
                <details style={{marginTop: '1.5em'}}>
                  <summary style={{cursor: 'pointer', color: '#2563eb', fontWeight: 600}}>Show raw response</summary>
                  <pre className="modal-raw-response">
                    {JSON.stringify(analysis, null, 2)}
                  </pre>
                </details>
                {analysis.error && (
                  <div className="modal-error">Error: {analysis.error}</div>
                )}
                {!analysis.analysis?.results?.length && !analysis.error && (
                  <div className="modal-no-issues">No issues found.</div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
