import React, { useState } from 'react';
import axios from 'axios';

interface AnalysisResult {
  kind: string;
  name: string;
  error: { Text: string }[];
  details: string;
}

interface Analysis {
  provider: string;
  errors: string | null;
  status: string;
  problems: number;
  results: AnalysisResult[];
}

interface ApiResponse {
  pod: string;
  analysis: Analysis;
  responsibility: string;
}

const App: React.FC = () => {
  const [namespace, setNamespace] = useState('');
  const [podName, setPodName] = useState('');
  const [response, setResponse] = useState<ApiResponse | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const res = await axios.post<ApiResponse>('http://localhost:8000/analyze', {
        namespace,
        pod_name: podName,
      });
      setResponse(res.data);
    } catch (err: any) {
      setError(err.message || 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: '800px', margin: '2rem auto', fontFamily: 'Arial' }}>
      <h1>Pod Analyzer</h1>
      <form onSubmit={handleSubmit} style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          placeholder="Namespace"
          value={namespace}
          onChange={(e) => setNamespace(e.target.value)}
          required
          style={{ marginRight: '0.5rem', padding: '0.5rem' }}
        />
        <input
          type="text"
          placeholder="Pod Name"
          value={podName}
          onChange={(e) => setPodName(e.target.value)}
          required
          style={{ marginRight: '0.5rem', padding: '0.5rem' }}
        />
        <button type="submit" style={{ padding: '0.5rem 1rem' }}>
          Analyze
        </button>
      </form>

      {loading && <p>Analyzing...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {response && (
        <div>
          <h2>Analysis Result for Pod: {response.pod}</h2>
          <p><strong>Status:</strong> {response.analysis.status}</p>
          <p><strong>Problems Detected:</strong> {response.analysis.problems}</p>
          <p><strong>Responsibility:</strong> {response.responsibility}</p>

          {response.analysis.results.map((result: AnalysisResult, index: number) => (
            <div key={index} style={{ border: '1px solid #ccc', padding: '1rem', marginTop: '1rem' }}>
              <p><strong>Kind:</strong> {result.kind}</p>
              <p><strong>Name:</strong> {result.name}</p>
              <p><strong>Details:</strong> {result.details}</p>
              <ul>
                {result.error.map((err: { Text: string }, i: number) => (
                  <li key={i}>{err.Text}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
