import React, { useState } from "react";

type Pod = {
  name: string;
  status: string;
};

const App: React.FC = () => {
  const [pods, setPods] = useState<Pod[]>([
    { name: "frontend-pod-1", status: "Healthy" },
    { name: "backend-pod-2", status: "Unhealthy" },
    { name: "db-pod-3", status: "Healthy" },
    { name: "cache-pod-4", status: "Unhealthy" },
  ]);

  const [selectedPod, setSelectedPod] = useState<Pod | null>(null);
  const [analyzedIssues, setAnalyzedIssues] = useState<string[] | null>(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);

  const unhealthyPods = pods.filter((pod) => pod.status === "Unhealthy");

  const handleAnalyze = async (pod: Pod) => {
    setSelectedPod(pod);
    setModalOpen(true);
    setLoading(true);
    setErrorMessage(null);
    setAnalyzedIssues(null);

    try {
      const res = await fetch("http://localhost:8000/analyze", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ pod_name: pod.name }),
      });

      if (!res.ok) {
        throw new Error("Failed to fetch analysis");
      }

      const data = await res.json();
      setAnalyzedIssues(data.issues || []);
    } catch (err: any) {
      setErrorMessage(err.message || "Unexpected error");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <h1 className="text-3xl font-bold text-center text-blue-800 mb-8">ðŸš‘ HealOps Dashboard</h1>

      <div className="max-w-2xl mx-auto bg-white p-6 rounded-xl shadow-md">
        <h2 className="text-xl font-semibold text-gray-700 mb-4">Unhealthy Pods</h2>

        {unhealthyPods.length === 0 ? (
          <p className="text-green-600">All pods are healthy ðŸŽ‰</p>
        ) : (
          <ul className="space-y-4">
            {unhealthyPods.map((pod) => (
              <li key={pod.name} className="flex items-center justify-between bg-red-100 px-4 py-2 rounded">
                <span className="text-red-800 font-medium">{pod.name}</span>
                <button
                  onClick={() => handleAnalyze(pod)}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-1.5 rounded transition duration-200"
                >
                  Analyze
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Modal */}
      {modalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white w-full max-w-md p-6 rounded-lg shadow-lg relative">
            <button
              onClick={() => setModalOpen(false)}
              className="absolute top-2 right-2 text-gray-500 hover:text-red-600"
            >
              âœ–
            </button>

            <h3 className="text-xl font-bold text-gray-800 mb-4">
              Analysis for <span className="text-blue-600">{selectedPod?.name}</span>
            </h3>

            {loading ? (
              <div className="flex justify-center items-center">
                <div className="animate-spin rounded-full h-8 w-8 border-t-4 border-blue-600 border-solid"></div>
              </div>
            ) : errorMessage ? (
              <p className="text-red-600">Error: {errorMessage}</p>
            ) : analyzedIssues && analyzedIssues.length > 0 ? (
              <ul className="list-disc pl-5 space-y-1 text-gray-700">
                {analyzedIssues.map((issue, i) => (
                  <li key={i}>{issue}</li>
                ))}
              </ul>
            ) : (
              <p className="text-green-600">No issues found ðŸŽ‰</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
