import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  // Pod state
  const [pods, setPods] = useState<{ healthy: any[]; unhealthy: any[] }>({ healthy: [], unhealthy: [] });
  const [loading, setLoading] = useState(true);
  const [analysis, setAnalysis] = useState<any>(null);
  const [analyzingPod, setAnalyzingPod] = useState<{ name: string; namespace: string } | null>(null);
  const [showModal, setShowModal] = useState(false);

  // Fetch pods
  useEffect(() => {
    fetch('/pods')
      .then((res) => res.json())
      .then((data) => {
        setPods(data);
        setLoading(false);
      });
  }, []);

  // Analyze pod
  const handleAnalyze = async (pod: { name: string; namespace: string }) => {
    setAnalyzingPod(pod);
    setShowModal(true);
    setAnalysis(null);
    try {
      const res = await fetch('/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ pod_name: pod.name, namespace: pod.namespace }),
      });
      const data = await res.json();
      setAnalysis(data);
    } catch (err) {
      setAnalysis({ error: String(err) });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-50 to-blue-50 p-6">
      <h1 className="text-4xl font-extrabold mb-8 text-center text-blue-700 drop-shadow">Kubernetes Pod Health Analyzer</h1>
      {loading ? (
        <div className="text-center text-blue-600 font-semibold">Loading pods...</div>
      ) : (
        <div className="max-w-2xl mx-auto flex flex-col gap-10">
          {/* Healthy Pods */}
          <div className="bg-white rounded-xl shadow-lg p-6 border-l-8 border-green-400">
            <h2 className="text-2xl font-bold mb-4 flex items-center gap-2 text-green-700">
              <span className="text-green-500 text-2xl">✅</span> Healthy Pods
            </h2>
            <ul className="space-y-3">
              {pods.healthy.map((pod) => (
                <li key={pod.name + pod.namespace} className="bg-green-50 rounded-lg px-4 py-3 flex items-center border border-green-200">
                  <span className="font-medium text-green-800">{pod.name}</span>
                  <span className="ml-2 text-xs text-green-600">({pod.namespace})</span>
                </li>
              ))}
              {pods.healthy.length === 0 && <li className="text-gray-400">No healthy pods</li>}
            </ul>
          </div>
          {/* Unhealthy Pods */}
          <div className="bg-white rounded-xl shadow-lg p-6 border-l-8 border-blue-400">
            <h2 className="text-2xl font-bold mb-4 flex items-center gap-2 text-blue-700">
              <span className="text-blue-500 text-2xl">❌</span> Unhealthy Pods
            </h2>
            <ul className="space-y-3">
              {pods.unhealthy.map((pod) => (
                <li key={pod.name + pod.namespace} className="bg-blue-50 rounded-lg px-4 py-3 flex items-center justify-between border border-blue-200">
                  <div>
                    <span className="font-medium text-blue-800">{pod.name}</span>
                    <span className="ml-2 text-xs text-blue-600">({pod.namespace})</span>
                  </div>
                  <button
                    className="bg-gradient-to-r from-blue-500 to-green-400 text-white px-5 py-2 rounded-lg font-semibold shadow hover:from-blue-600 hover:to-green-500 transition"
                    onClick={() => handleAnalyze(pod)}
                  >
                    Analyze
                  </button>
                </li>
              ))}
              {pods.unhealthy.length === 0 && <li className="text-gray-400">No unhealthy pods</li>}
            </ul>
          </div>
        </div>
      )}
      {/* Modal for analysis */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-gradient-to-br from-green-100 to-blue-100 rounded-2xl shadow-2xl p-8 w-full max-w-2xl relative border-4 border-blue-300 animate-fadeIn">
            <button
              className="absolute top-3 right-3 text-blue-400 hover:text-blue-700 text-2xl font-bold"
              onClick={() => setShowModal(false)}
              aria-label="Close analysis modal"
            >
              ×
            </button>
            <h3 className="text-2xl font-extrabold mb-6 text-blue-700 text-center">Analysis for <span className="text-green-700">{analyzingPod?.name}</span></h3>
            {!analysis ? (
              <div className="text-center text-blue-600 font-semibold">Analyzing...</div>
            ) : (
              <div>
                {/* Always show the full backend response for debugging */}
                <div className="mb-6">
                  <div className="font-semibold mb-1 text-blue-700">Raw Backend Response:</div>
                  <pre className="text-xs whitespace-pre-wrap bg-white rounded-lg p-4 border border-blue-200 text-blue-900 overflow-x-auto">
                    {JSON.stringify(analysis, null, 2)}
                  </pre>
                </div>
                {/* Show issues if present */}
                {Array.isArray(analysis.analysis) && analysis.analysis.length > 0 ? (
                  <ul className="space-y-3">
                    {analysis.analysis.map((item: any, idx: number) => (
                      <li key={idx} className="bg-blue-50 rounded-lg p-4 border border-blue-200">
                        <div className="font-semibold text-blue-800">{item.name}</div>
                        <pre className="text-xs mt-2 whitespace-pre-wrap text-blue-900">{JSON.stringify(item.issues, null, 2)}</pre>
                      </li>
                    ))}
                  </ul>
                ) : analysis.error ? (
                  <div className="text-red-500 font-semibold">Error: {analysis.error}</div>
                ) : (
                  <div className="text-green-700 font-semibold">No issues found.</div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
