import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [pods, setPods] = useState<{ healthy: any[]; unhealthy: any[] }>({ healthy: [], unhealthy: [] });
  const [loading, setLoading] = useState(true);
  const [analysis, setAnalysis] = useState<any>(null);
  const [analyzingPod, setAnalyzingPod] = useState<{ name: string; namespace: string } | null>(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetch('/pods')
      .then((res) => res.json())
      .then((data) => {
        setPods(data);
        setLoading(false);
      });
  }, []);

  const handleAnalyze = async (pod: { name: string; namespace: string }) => {
    if (analyzingPod?.name === pod.name && analyzingPod?.namespace === pod.namespace && analysis) {
      setShowModal(true);
      return;
    }

    setAnalyzingPod(pod);
    setShowModal(true);
    setAnalysis(null);
    try {
      const res = await fetch('/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ pod_name: pod.name, namespace: pod.namespace }),
      });
      const data = await res.json();
      setAnalysis(data);
    } catch (err) {
      setAnalysis({ error: String(err) });
    }
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    alert("Command copied to clipboard");
  };

  // Helper to render all issues from the new backend response format
  function renderAllIssues(analysis: any) {
    if (!analysis || !Array.isArray(analysis.analysis) || !analysis.analysis[0]?.results) return null;
    const results = analysis.analysis[0].results;
    return (
      <div className="processed-analysis">
        {results.map((result: any, idx: number) => (
          <div key={idx} className="modal-issue-item">
            <div className="modal-issue-name">{result.kind}: <span className="main-pod-issue-name">{result.name}</span></div>
            {result.parentObject && <div className="modal-parent-object">Parent: {result.parentObject}</div>}
            {Array.isArray(result.error) && result.error.length > 0 && (
              <ul className="modal-error-list">
                {result.error.map((err: any, eidx: number) => (
                  <li key={eidx} className="modal-error-text">{err.Text}</li>
                ))}
              </ul>
            )}
            {result.details && (
              <div className="modal-issue-details">
                <strong>How to fix:</strong>
                <pre className="modal-issue-solution">{result.details.trim()}</pre>
              </div>
            )}
          </div>
        ))}
      </div>
    );
  }

  function renderFixSuggestions(fixes: string[] = []) {
    if (fixes.length === 0) return null;

    return (
      <div className="fix-suggestions">
        <h4>Suggested Commands:</h4>
        <ul className="fix-list">
          {fixes.map((cmd, idx) => (
            <li key={idx} className="fix-item">
              <pre>{cmd}</pre>
              <button
                className="copy-btn"
                onClick={() => copyToClipboard(cmd)}
              >
                Copy
              </button>
            </li>
          ))}
        </ul>
      </div>
    );
  }

  return (
    <div className="app-bg">
      <h1 className="app-title">HealOps - Kubernetes AI Analyzer</h1>
      {loading ? (
        <div className="loading">Loading pods...</div>
      ) : (
        <div className="pod-sections">
          <div className="pod-card healthy">
            <h2 className="pod-section-title healthy">✅ Healthy Pods</h2>
            <ul className="pod-list">
              {pods.healthy.map((pod) => (
                <li key={pod.name + pod.namespace} className="pod-list-item healthy">
                  <span className="pod-name">{pod.name}</span>
                  <span className="pod-namespace">({pod.namespace})</span>
                </li>
              ))}
              {pods.healthy.length === 0 && <li className="pod-empty">No healthy pods</li>}
            </ul>
          </div>

          <div className="pod-card unhealthy">
            <h2 className="pod-section-title unhealthy">❌ Unhealthy Pods</h2>
            <ul className="pod-list">
              {pods.unhealthy.map((pod) => (
                <li key={pod.name + pod.namespace} className="pod-list-item unhealthy">
                  <div>
                    <span className="pod-name">{pod.name}</span>
                    <span className="pod-namespace">({pod.namespace})</span>
                  </div>
                  <button
                    className="analyze-btn"
                    onClick={() => handleAnalyze(pod)}
                  >
                    Analyze
                  </button>
                </li>
              ))}
              {pods.unhealthy.length === 0 && <li className="pod-empty">No unhealthy pods</li>}
            </ul>
          </div>
        </div>
      )}

      {/* Modal */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-window">
            <button
              className="modal-close"
              onClick={() => setShowModal(false)}
              aria-label="Close analysis modal"
            >
              ×
            </button>
            <h3 className="modal-title">Analysis for <span className="modal-pod-name">{analyzingPod?.name}</span></h3>

            {!analysis ? (
              <div className="modal-loading">Analyzing...</div>
            ) : (
              <div>
                {/* Processed, user-friendly analysis */}
                {renderAllIssues(analysis)}
                {renderFixSuggestions(analysis?.suggested_fixes)}
                <details style={{ marginTop: '1.5em' }}>
                  <summary style={{ cursor: 'pointer', color: '#2563eb', fontWeight: 600 }}>Show raw response</summary>
                  <pre className="modal-raw-response">
                    {JSON.stringify(analysis, null, 2)}
                  </pre>
                </details>
                {analysis.error && (
                  <div className="modal-error">Error: {analysis.error}</div>
                )}
                {!analysis.analysis?.results?.length && !analysis.error && (
                  <div className="modal-no-issues">No issues found.</div>
                )}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
